import React, {useState} from 'react';
import './App.css';
import Card from './components/Card';
import NewCardPopUp from './components/NewCardPopUp';

function App() {
  const [cards, setCardsList] = useState([]);
  const [card, setNewCard] = useState({
    front: '',
    back: ''
  });
  const [popUp, showPopUp] = useState(false);
  const [emptyFieldsError, setEmptyFieldsError] = useState({ isEmpty: false, error: ''});
  const toggleCardPopUp = _ => {
    showPopUp(!popUp);
  }

  return (
    <div className="study-cards">
      <h1 className="title">Study Cards</h1>
      <div className="new-btn-container">
        <p className="greeter">Create study cards to test yourself and master your knowledge! Hover over each created card to reveal the answer.</p>
        <button className="new-card-btn" onClick={toggleCardPopUp}>New Card</button>
      </div>
      <div className="cards-container">
        { cards.length ? cards.map((card, index) => {
          return <Card card={card} key={index} />;
        }) : <div>You haven't created any cards yet, click the 'New Card' button to start quizzing yourself!</div>}
      </div>
      { popUp ? <NewCardPopUp
        card={card}
        setNewCard={setNewCard}
        cards={cards}
        togglePopUp={toggleCardPopUp}
        setCardsList={setCardsList}
        /> : null }
    </div>
  );
}

export default App;
