import React, {useState} from 'react';
import './App.css';
import Card from './components/Card';
import NewCardPopUp from './components/NewCardPopUp';

function App() {
  const [cards, setCardsList] = useState([]);
  const [individualCard, setNewCard] = useState({
    front: '',
    back: ''
  })
  const [popUp, showPopUp] = useState(false)
  const toggleCardPopUp = _ => {
    showPopUp(!popUp);
  }

  return (
    <div className="study-cards">
      <h1>Study Cards</h1>
      { cards.length ? cards.map(card => {
        return <Card />;
      }) : <div> no cards right now</div>}
      <button className="new-card-btn" onClick={toggleCardPopUp}>New Card</button>
      { popUp ? <NewCardPopUp setNewCard={setIndividualCard} cards={cards} togglePopUp={toggleCardPopUp} /> : null }
    </div>
  );
}

export default App;
