import React from 'react';

const NewCardPopUp = ({togglePopUp}) => {

  const handleToggle = e => {
    console.log(e.target);
    togglePopUp();
  }
  return (
    <div className="popup-overlay" onClick={handleToggle}>
      <div className="new-card-component">
        <div className="close-popup" onClick={handleToggle}>X</div>
        <label for="card-front">What would you like the front of the card to display?</label>
        <input type="text" id="card-front" className="input-new-card" />
        <label for="card-front">What would you like the back of the card to display?</label>
        <input type="text" id="card-back" className="input-new-card" />
        <button className="add-card-btn">Add Card</button>
      </div>
    </div>
  )
}

export default NewCardPopUp;
