import React from 'react';

const NewCardPopUp = ({togglePopUp, cards, card, setNewCard}) => {

  const handleToggle = e => {
    togglePopUp();
  }

  const submitNewCard = e => {
    e.preventDefault();
    const cardsPile = [...cards];
    const newCard = {
      e
    }
    console.log('submitted!')
  }

  const handleInputChange = e => {
    e.preventDefault();
    const cardItem = {
      [e.target.name] : e.target.value
    }
    const finalCard = [...card, cardItem];
    setNewCard(cardItem);
    console.log(card);
  }

  return (
      <div>
        <div className="popup-overlay" onClick={handleToggle}>
        </div>
        <form className="new-card-component" onSubmit={submitNewCard}>
          <div className="close-popup" onClick={handleToggle}>X</div>
          <label htmlFor="card-front">What would you like the front of the card to display?</label>
          <input type="text" name="card-front" onChange={handleInputChange} id="card-front" className="input-new-card" />
          <label htmlFor="card-front">What would you like the back of the card to display?</label>
          <input type="text" id="card-back" onChange={handleInputChange} name="card-back" className="input-new-card" />
          <input type="submit" value="Add Card" className="add-card-btn" />
        </form>
      </div>
  )
}

export default NewCardPopUp;
